mapper.update:
update order_header
SET 
  update_by = #{updateBy,jdbcType=VARCHAR},
  update_date = #{updateDate,jdbcType=TIMESTAMP},
  order_status = #{orderStatus,typeHandler=com.jeeplus.common.enums.api.impl.IntegerValueEnumTypeHandler}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT oh.id, oh.order_number, oh.order_date, oh.order_status, oh.remit_code, oh.cancel_date, oh.create_by, oh.create_date, oh.update_by, oh.update_date, oh.remarks, oh.del_flag, ol.id as line_id, ol.line_status, ol.header_id, ol.goods_name, ol.buy_num, ol.unit_price, ol.discount_price, ol.specifications, ol.amount, ol.message, ol.create_by as line_create_by, ol.create_date as line_create_date, ol.update_by as line_update_by, ol.update_date as line_update_date, ol.remarks as line_remarks, ol.del_flag as line_del_flag FROM order_header oh left join order_line ol on oh.id = ol.header_id and ol.del_flag = '0' WHERE oh.del_flag = '0'
and oh.create_by = #{createBy,jdbcType=VARCHAR}
and oh.remarks = #{remarks,jdbcType=VARCHAR}
and oh.order_number like CONCAT(CONCAT('%',#{orderNumber,jdbcType=VARCHAR}),'%')
and oh.order_date = #{orderDate,jdbcType=TIMESTAMP}
and oh.order_status = #{orderStatus,typeHandler=com.jeeplus.common.enums.api.impl.IntegerValueEnumTypeHandler}
and oh.remit_code = #{remitCode,jdbcType=VARCHAR}
order by oh.order_number desc, ol.id desc
